const items = $input.all().map(i => i.json);

// Nos quedamos SOLO con los campos necesarios
const pedidos = items.map(r => ({
  numeroPedido: r.numeroPedido,
  cliente: r.cliente,
  titulo: r.titulo,
  faseInterior1: r.faseInterior1,
  faseCubierta: r.faseCubierta,
  fechaPrevistaEntrega: String(r.fechaPrevistaEntrega || "").slice(0, 10),
  sinRetraso: r.sinRetraso,
  encuadernacion: r.encuadernacion,
  unionPliegos: r.unionPliegos,
}));

// Ordenar por fecha y luego por cliente (opcional, muy útil)
pedidos.sort((a, b) => {
  const d = String(a.fechaPrevistaEntrega).localeCompare(String(b.fechaPrevistaEntrega));
  if (d !== 0) return d;
  return String(a.cliente).localeCompare(String(b.cliente));
});

return [{
  json: {
    generatedAt: new Date().toISOString(),
    totalPedidos: pedidos.length,
    pedidos,
  }
}];

const excluded = new Set([
  "10.0. Facturado",
  "99.0. Cancelado",
  "9.0. Mercancía entregada",
]);

const input = $input.all().map(i => i.json);

// Normalizar: si viene un array dentro de 1 solo item
let rows = [];
if (input.length === 1 && Array.isArray(input[0])) {
  rows = input[0];
} else {
  rows = input;
}

// --- Helpers fechas (sin librerías) ---
function startOfDayLocal(d) {
  return new Date(d.getFullYear(), d.getMonth(), d.getDate());
}

function parseDateOnlyLocal(dateStr) {
  const s = String(dateStr || "").slice(0, 10); // YYYY-MM-DD
  const [y, m, d] = s.split("-").map(Number);
  if (!y || !m || !d) return null;
  return new Date(y, m - 1, d); // local midnight
}

function addBusinessDaysLocal(startDate, days) {
  let d = startOfDayLocal(startDate);
  let added = 0;

  while (added < days) {
    d = new Date(d.getFullYear(), d.getMonth(), d.getDate() + 1);
    const wd = d.getDay(); // 0=Dom, 6=Sáb
    if (wd !== 0 && wd !== 6) added++;
  }
  return d;
}

// Umbral = hoy + 5 días laborables
const today = startOfDayLocal(new Date());
const threshold = addBusinessDaysLocal(today, 5);

// Filtrar: estados + fechaAlta año 2026 + fechaPrevistaEntrega (hoy..umbral)
const filtered = rows.filter(r => {
  const faseInterior1 = String(r.faseInterior1 || "").trim();
  const faseCubierta  = String(r.faseCubierta  || "").trim();

  // Excluir si cualquiera está en la lista
  if (excluded.has(faseInterior1) || excluded.has(faseCubierta)) return false;

  // fechaAlta año 2026 (formato "YYYY-MM-DD HH:mm:ss")
  const fechaAlta = String(r.fechaAlta || "");
  const yearAlta = Number(fechaAlta.slice(0, 4));
  if (yearAlta !== 2026) return false;

  // fechaPrevistaEntrega dentro de 5 días laborables (incluye hoy)
  const fpe = parseDateOnlyLocal(r.fechaPrevistaEntrega);
  if (!fpe) return false;

return fpe <= threshold;

});

// Devolver items (uno por pedido)
return filtered.map(r => ({ json: r }));
