const fileId = $items("Subir PDF a OpenAI")[0].json.id;  // ✅ file-...
const prompt = ($json.promptCabecera || "").trim();

if (!fileId?.startsWith("file-")) {
  throw new Error(`fileId inválido (esperaba file-...): ${fileId}`);
}

const schema = {
  type: "object",
  additionalProperties: false,
  properties: {
    // Identificación / cabecera
    cliente: { type: ["string", "integer"] },
    contacto: { type: ["string", "integer"] },
    suReferencia: { type: "string" },

    // En algunos PDFs viene como "002/006", así que lo dejamos flexible
    numeroOf: { type: ["string", "integer"] },

    numeroOtCliente: { type: "string" },
    isbnPack: { type: "string" },

    // Fechas
    fechaAlta: { type: "string" },
    fechaEntrega: { type: "string" },

    // Pedido
    sinRetraso: { type: "boolean" },
    tirada: { type: "integer" },
    precioUnitarioSinIVA: { type: "number" },
    precioToltalSinIVA: { type: "number" },
    quiereVerFerros: { type: "integer" },
    certificacion: { type: "integer" },
    observacionesPedido: { type: "string" },

    // Libro / proyecto
    titulo: { type: "string" },
    sello: { type: "string" },
    isbn: { type: "string" },
    codProyecto: { type: "string" },

    // Formato
    anchoPaginaCm: { type: "number" },
    altoPaginaCm: { type: "number" },

    // Encuadernación (base)
    unionPliegos: { type: "integer" },
    encuadernacion: { type: "integer" },

    // En tu plantilla son 0/1, pero a veces el modelo devuelve true/false → permitimos ambos
    solapa: { type: ["integer", "boolean"] },
    hendidoCortesia: { type: ["integer", "boolean"] },

    observacionesEncuadernacion: { type: "string" },

    // Encuadernación (extras)
    cabezada: { type: "integer" },
    cabezadaColorOtro: { type: "string" },

    guardasAparte: { type: "integer" },
    guardasImpresas: { type: "integer" },

    cintaRegistro: { type: "integer" },
    cintaRegistroColorOtro: { type: "string" },

    calibreCarton: { type: "integer" },
    tipoLomo: { type: "integer" },

    // Espiral
    espiralMaterial: { type: "integer" },
    espiralColor: { type: "integer" },
    espiralColorOtro: { type: "string" },
    espiralLado: { type: "integer" },

    // Wire-o
    wireoMaterial: { type: "integer" },
    wireoColor: { type: "integer" },
    wireoColorOtro: { type: "string" },
    wireoLado: { type: "integer" }
  },
  required: [] // nada requerido
};

return {
  openaiBody: {
    model: "gpt-5.2",
    input: [{
      role: "user",
      content: [
        { type: "input_file", file_id: fileId },
        { type: "input_text", text: prompt }
      ]
    }],
    text: {
      format: {
        type: "json_schema",
        name: "cabecera_pedido",
        strict: false,
        schema
      }
    }
  }
};

// Soporta "Full Response" ON/OFF:
const body = $json.body ?? $json;

const text =
  body.output?.flatMap(o => o.content || [])
    ?.find(c => c.type === "output_text")
    ?.text;

if (!text) throw new Error("No encuentro output_text en la respuesta de OpenAI");

let data;
try {
  data = JSON.parse(text);
} catch (e) {
  throw new Error("El output_text no es JSON parseable: " + e.message + "\n\n" + text);
}

// Limpieza: borra 0, "", null, [], {} EXCEPTO estos campos:
const keepAlways = new Set([
  "cliente", "contacto", "sinRetraso", "unionPliegos", "encuadernacion", "solapa", "hendidoCortesia"
]);

function isEmpty(v) {
  if (v === null || v === undefined) return true;
  if (typeof v === "string" && v.trim() === "") return true;
  if (typeof v === "number" && v === 0) return true;
  if (Array.isArray(v) && v.length === 0) return true;
  if (typeof v === "object" && !Array.isArray(v) && Object.keys(v).length === 0) return true;
  return false;
}

for (const k of Object.keys(data)) {
  if (keepAlways.has(k)) continue;
  if (isEmpty(data[k])) delete data[k];
}

return [{ json: data }];

const fileId = $items("Subir PDF a OpenAI")[0].json.id; // ✅ file-...
const prompt = ($json.promptInteriores || "").trim();

if (!fileId?.startsWith("file-")) {
  throw new Error(`fileId inválido (esperaba file-...): ${fileId}`);
}

// --- Schema INTERIORES ---
const papelPedidoProps = {
  refPedido: { type: "string" },
  gramaje: { type: "integer" },
  anchoBobinaCm: { type: "number" },
  upsCalc: { type: "integer" },
  ups: { type: "integer" },
  metros: { type: "number" },
  kg: { type: "number" },
};

const interiorProps = {
  papel_id: { type: "integer" },
  propietarioPapel: { type: "integer" },
  tipoImpresion: { type: "integer" },
  calidad: { type: "string" },
  paginas: { type: "integer" },
  plegadoOCortado: { type: "integer" },
  maquina: { type: "integer" },
  observaciones: { type: "string" },
  papelPedido: {
    type: "object",
    additionalProperties: false,
    properties: papelPedidoProps,
    required: Object.keys(papelPedidoProps),
  },
};

const schema = {
  type: "object",
  additionalProperties: false,
  properties: {
    interiores: {
      type: "array",
      items: {
        type: "object",
        additionalProperties: false,
        properties: interiorProps,
        required: Object.keys(interiorProps),
      },
    },
  },
  required: ["interiores"],
};

return {
  openaiBody: {
    model: "gpt-5.2",
    input: [
      {
        role: "user",
        content: [
          { type: "input_file", file_id: fileId },
          { type: "input_text", text: prompt },
        ],
      },
    ],
    text: {
      format: {
        type: "json_schema",
        name: "interiores_pedido",
        strict: false,
        schema,
      },
    },
  },
};

// -----------------------------
// PARSE + LIMPIEZA (INTERIORES)
// -----------------------------

// A veces n8n trae el body como string. Lo hacemos robusto:
let resp = $json;
if (typeof resp === "string") resp = JSON.parse(resp);

// Soporta "Full Response" ON/OFF:
const body = resp.body ?? resp;

// En Responses API el texto suele venir en body.output[0..].content[*].text
const text =
  body.output?.flatMap(o => o.content || [])
    ?.find(c => c.type === "output_text")
    ?.text;

if (!text) throw new Error("No encuentro output_text en la respuesta de OpenAI");

let parsed;
try {
  parsed = JSON.parse(text);
} catch (e) {
  throw new Error("El output_text no es JSON parseable: " + e.message + "\n\n" + text);
}

// Esperamos { interiores: [ ... ] } (si el modelo devuelve directo el array, lo aceptamos)
let interiores = Array.isArray(parsed?.interiores)
  ? parsed.interiores
  : (Array.isArray(parsed) ? parsed : []);

// ---------- LIMPIEZA ----------
// Borra 0, "", null, [], {} excepto estos PATHS (para no romper estructura mínima)
const keepAlwaysPaths = new Set([
  // Mantener siempre el array (aunque quede vacío)
  "interiores",

  // Campos mínimos por interior
  "interiores[].papel_id",
  "interiores[].propietarioPapel",
  "interiores[].tipoImpresion",
  "interiores[].paginas",

  // Mantener siempre el objeto papelPedido y claves útiles
  "interiores[].papelPedido",
  "interiores[].papelPedido.gramaje",
  "interiores[].papelPedido.anchoBobinaCm",
  "interiores[].papelPedido.ups",
  "interiores[].papelPedido.kg",
]);

function isEmpty(v) {
  if (v === null || v === undefined) return true;
  if (typeof v === "string" && v.trim() === "") return true;
  if (typeof v === "number" && v === 0) return true;
  if (Array.isArray(v) && v.length === 0) return true;
  if (typeof v === "object" && !Array.isArray(v) && Object.keys(v).length === 0) return true;
  return false;
}

function cleanDeep(value, path) {
  // Arrays: limpiar elementos y eliminar elementos vacíos
  if (Array.isArray(value)) {
    const arr = [];
    for (const el of value) {
      const cleaned = cleanDeep(el, path + "[]");
      // si el elemento queda vacío, lo descartamos
      if (!isEmpty(cleaned)) arr.push(cleaned);
    }
    return arr;
  }

  // Objetos: limpiar claves recursivamente
  if (value && typeof value === "object") {
    const obj = { ...value };
    for (const k of Object.keys(obj)) {
      const childPath = path ? `${path}.${k}` : k;

      obj[k] = cleanDeep(obj[k], childPath);

      // Si está en keepAlwaysPaths, NO lo borramos aunque esté vacío
      if (keepAlwaysPaths.has(childPath)) continue;

      // Si está vacío, borramos
      if (isEmpty(obj[k])) delete obj[k];
    }
    return obj;
  }

  // Primitivos
  return value;
}

// Aplicar limpieza a cada interior
interiores = cleanDeep(interiores, "interiores");

// Asegurar que siempre devolvemos el array
if (!Array.isArray(interiores)) interiores = [];

return [{
  json: {
    interiores
  }
}];

const fileId = $items("Subir PDF a OpenAI")[0].json.id; // ✅ file-...
const prompt = ($json.promptCubierta || "").trim();

if (!fileId?.startsWith("file-")) {
  throw new Error(`fileId inválido (esperaba file-...): ${fileId}`);
}

// --- Schema CUBIERTA ---
const cubiertaProps = {
  materialCubierta: { type: "integer" },
  propietarioPapel: { type: "integer" },
  plastificado: { type: "integer" },
  uvi: { type: "integer" },
  impresion: { type: "string" },      // ✅ en tu prompt es tipo "4/0", "4/4"...
  codDescarga: { type: "integer" },   // si luego quieres omitirlo cuando 0, lo haces al final
  tecnologia: { type: "integer" },
  observaciones: { type: "string" },  // si luego quieres omitirlo cuando "", lo haces al final
};

const schema = {
  type: "object",
  additionalProperties: false,
  properties: {
    cubierta: {
      type: "object",
      additionalProperties: false,
      properties: cubiertaProps,
      // para evitar errores del validador de schema y para asegurar estructura estable:
      required: Object.keys(cubiertaProps),
    },
  },
  required: ["cubierta"],
};

return {
  openaiBody: {
    model: "gpt-5.2",
    input: [{
      role: "user",
      content: [
        { type: "input_file", file_id: fileId },
        { type: "input_text", text: prompt },
      ],
    }],
    text: {
      format: {
        type: "json_schema",
        name: "cubierta_pedido",
        strict: false,
        schema,
      },
    },
  },
};

// ---------------------------
// PARSE + LIMPIEZA (CUBIERTA)
// ---------------------------

// A veces n8n trae el body como string. Lo hacemos robusto:
let resp = $json;
if (typeof resp === "string") resp = JSON.parse(resp);

// Soporta "Full Response" ON/OFF:
const body = resp.body ?? resp;

// En Responses API el texto suele venir en body.output[0..].content[*].text
const text =
  body.output?.flatMap(o => o.content || [])
    ?.find(c => c.type === "output_text")
    ?.text;

if (!text) throw new Error("No encuentro output_text en la respuesta de OpenAI (cubierta)");

// Parse JSON
let parsed;
try {
  parsed = JSON.parse(text);
} catch (e) {
  throw new Error(
    `El output_text no es JSON válido: ${e.message}. Primeros 200 chars: ${String(text).slice(0, 200)}`
  );
}

// Normaliza salida: esperamos { cubierta: {...} }
// Si devuelve directamente el objeto de cubierta, lo envolvemos
const cubiertaRaw =
  (parsed && typeof parsed === "object" && !Array.isArray(parsed) && parsed.cubierta)
    ? parsed.cubierta
    : parsed;

// Validación mínima
if (!cubiertaRaw || typeof cubiertaRaw !== "object" || Array.isArray(cubiertaRaw)) {
  throw new Error("La salida parseada no contiene un objeto 'cubierta' válido");
}

// ---------- LIMPIEZA ----------
// Borra 0, "", null, [], {} EXCEPTO campos que deben existir siempre
const keepAlways = new Set([
  "materialCubierta",
  "propietarioPapel",
  "plastificado",
  "uvi",
  "impresion",
  "codDescarga",
  "tecnologia",
  // Si quieres forzar que observaciones siempre exista, añade "observaciones" aquí.
]);

function isEmpty(v) {
  if (v === null || v === undefined) return true;
  if (typeof v === "string" && v.trim() === "") return true;
  if (typeof v === "number" && v === 0) return true;
  if (Array.isArray(v) && v.length === 0) return true;
  if (typeof v === "object" && !Array.isArray(v) && Object.keys(v).length === 0) return true;
  return false;
}

// Limpieza superficial (cubierta suele ser plano)
const cubierta = { ...cubiertaRaw };
for (const k of Object.keys(cubierta)) {
  if (keepAlways.has(k)) continue;
  if (isEmpty(cubierta[k])) delete cubierta[k];
}

// Asegura que los campos "siempre" existen aunque el modelo los omitiera
for (const k of keepAlways) {
  if (!(k in cubierta)) {
    // defaults por tipo esperado
    if (k === "impresion") cubierta[k] = "";
    else cubierta[k] = 0;
  }
}

// Devuelve solo el bloque de cubierta
return [{
  json: {
    cubierta
  }
}];

const fileId = $items("Subir PDF a OpenAI")[0].json.id; // ✅ file-...
const prompt = ($json.promptExtras || "").trim();

if (!fileId?.startsWith("file-")) {
  throw new Error(`fileId inválido (esperaba file-...): ${fileId}`);
}

// ---------- Subschemas ----------

// papelPedido / papelProduccion (idénticos salvo nombre de ref)
const papelPedidoProps = {
  refPedido: { type: "string" },
  gramaje: { type: "integer" },
  anchoBobinaCm: { type: "number" },
  upsCalc: { type: "integer" },
  ups: { type: "integer" },
  metros: { type: "number" },
  kg: { type: "number" },
};

const papelProduccionProps = {
  refProduccion: { type: "string" },
  gramaje: { type: "integer" },
  anchoBobinaCm: { type: "number" },
  upsCalc: { type: "integer" },
  ups: { type: "integer" },
  metros: { type: "number" },
  kg: { type: "number" },
};

const guardasProps = {
  papel_id: { type: "integer" },
  propietarioPapel: { type: "integer" },
  tipoImpresion: { type: "integer" },
  calidad: { type: "string" },          // en tu prompt es texto ("Ahuesado", etc.)
  paginas: { type: "integer" },
  plegadoOCortado: { type: "integer" },
  maquina: { type: "integer" },
  observaciones: { type: "string" },
  papelPedido: {
    type: "object",
    additionalProperties: false,
    properties: papelPedidoProps,
    required: Object.keys(papelPedidoProps),
  },
  papelProduccion: {
    type: "object",
    additionalProperties: false,
    properties: papelProduccionProps,
    required: Object.keys(papelProduccionProps),
  },
};

const sobrecubiertaProps = {
  materialSobrecubierta: { type: "integer" },
  propietarioPapel: { type: "integer" },
  plastificado: { type: "integer" },
  uvi: { type: "integer" },
  impresion: { type: "string" },    // "4/0", "4/4", etc.
  tecnologia: { type: "integer" },
  observaciones: { type: "string" },
};

const fajaProps = {
  materialFaja: { type: "integer" },
  propietarioPapel: { type: "integer" },
  plastificado: { type: "integer" },
  uvi: { type: "integer" },
  impresion: { type: "string" },    // "4/0", "4/4", etc.
  tecnologia: { type: "integer" },
  observaciones: { type: "string" },
};

const componenteEspecialProps = {
  material: { type: "integer" },
  propietarioPapel: { type: "integer" },
  nombre: { type: "string" },
  plastificado: { type: "integer" },
  uvi: { type: "boolean" },         // en tu modelo inicial era boolean
  impresion: { type: "string" },    // mejor string ("4/0"...)
  tecnologia: { type: "integer" },
  observaciones: { type: "string" },
};

const peticionEspecialProps = {
  descripcion: { type: "string" },
};

const direccionProps = {
  direccionId: { type: "string" },  // a veces viene como número; si prefieres integer lo cambio
  destinatario: { type: "string" },
  via: { type: "string" },
  numero: { type: "string" },
  piso: { type: "string" },
  puerta: { type: "string" },
  cp: { type: "string" },
  localidad: { type: "string" },
  provincia: { type: "string" },
  pais: { type: "string" },
  contacto: { type: "string" },
  telefonos: { type: "string" },
  pallet: { type: "integer" },
  encajado: { type: "integer" },
  ejemplaresPaquete: { type: "integer" },
  ejemplares: { type: "integer" },
  envioResto: { type: "integer" },
  modelos: { type: "integer" },
  observaciones: { type: "string" },
};

// ---------- Main schema ----------
const schema = {
  type: "object",
  additionalProperties: false,
  properties: {
    guardas: {
      type: "object",
      additionalProperties: false,
      properties: guardasProps,
      required: Object.keys(guardasProps),
    },
    sobrecubierta: {
      type: "object",
      additionalProperties: false,
      properties: sobrecubiertaProps,
      required: Object.keys(sobrecubiertaProps),
