      items: {
        type: "object",
        additionalProperties: false,
        properties: peticionEspecialProps,
        required: Object.keys(peticionEspecialProps),
      },
    },
    direcciones: {
      type: "array",
      items: {
        type: "object",
        additionalProperties: false,
        properties: direccionProps,
        required: Object.keys(direccionProps),
      },
    },
  },
  required: ["guardas", "sobrecubierta", "faja", "componentesEspeciales", "peticionesEspeciales", "direcciones"],
