return {
  openaiBody: {
    model: "gpt-5.2",
    input: [{
      role: "user",
      content: [
        { type: "input_file", file_id: fileId },
        { type: "input_text", text: prompt },
      ],
    }],
    text: {
      format: {
        type: "json_schema",
        name: "extras_pedido",
        strict: false,
        schema,
      },
    },
  },
};

// ------------------------
// PARSE + LIMPIEZA (EXTRAS)
// ------------------------

// A veces n8n trae el body como string. Lo hacemos robusto:
let resp = $json;
if (typeof resp === "string") resp = JSON.parse(resp);

// Soporta "Full Response" ON/OFF:
const body = resp.body ?? resp;

// En Responses API el texto suele venir en body.output[0..].content[*].text
const text =
  body.output?.flatMap(o => o.content || [])
    ?.find(c => c.type === "output_text")
    ?.text;

if (!text) {
  throw new Error("No encuentro output_text en la respuesta de OpenAI (extras)");
}

// Parse JSON
let parsed;
try {
  parsed = JSON.parse(text);
} catch (e) {
  throw new Error(
    `El output_text no es JSON válido: ${e.message}. Primeros 200 chars: ${String(text).slice(0, 200)}`
  );
}

if (!parsed || typeof parsed !== "object" || Array.isArray(parsed)) {
  throw new Error("La salida parseada de extras no es un objeto JSON válido");
}

// -----------------
// Normalización base
// -----------------
const norm = {
  guardas: parsed.guardas ?? null,
  sobrecubierta: parsed.sobrecubierta ?? null,
  faja: parsed.faja ?? null,
  componentesEspeciales: Array.isArray(parsed.componentesEspeciales) ? parsed.componentesEspeciales : [],
  peticionesEspeciales: Array.isArray(parsed.peticionesEspeciales) ? parsed.peticionesEspeciales : [],
  direcciones: Array.isArray(parsed.direcciones) ? parsed.direcciones : [],
};

// -----------
// Limpieza
// -----------
// Borra 0, "", null, [], {} EXCEPTO algunos campos críticos (por path)
const keepAlwaysPaths = new Set([
  // Arrays raíz: queremos que existan aunque estén vacíos
  "componentesEspeciales",
  "peticionesEspeciales",
  "direcciones",

  // Direcciones: mantener siempre si existen (si vienen vacíos, se limpiarán igual)
  "direcciones[].direccionId",
  "direcciones[].ejemplares",
]);

function isEmpty(v) {
  if (v === null || v === undefined) return true;
  if (typeof v === "string" && v.trim() === "") return true;
  if (typeof v === "number" && v === 0) return true;
  if (Array.isArray(v) && v.length === 0) return true;
  if (typeof v === "object" && !Array.isArray(v) && Object.keys(v).length === 0) return true;
  return false;
}

function cleanDeep(value, path) {
  if (Array.isArray(value)) {
    const arr = [];
    for (const el of value) {
      const cleaned = cleanDeep(el, path + "[]");
      if (!isEmpty(cleaned)) arr.push(cleaned);
    }
    return arr;
  }

  if (value && typeof value === "object") {
    const obj = { ...value };
    for (const k of Object.keys(obj)) {
      const childPath = path ? `${path}.${k}` : k;
      obj[k] = cleanDeep(obj[k], childPath);

      if (keepAlwaysPaths.has(childPath)) continue;
      if (isEmpty(obj[k])) delete obj[k];
    }
    return obj;
  }

  return value;
}

// Limpia cada bloque
const cleaned = cleanDeep(norm, "");

// Post-reglas:
// - Si guardas/sobrecubierta/faja quedan vacíos => null
function nullIfEmptyObject(v) {
  if (!v || typeof v !== "object" || Array.isArray(v)) return v;
  return Object.keys(v).length === 0 ? null : v;
}

const out = {
  guardas: nullIfEmptyObject(cleaned.guardas),
  sobrecubierta: nullIfEmptyObject(cleaned.sobrecubierta),
  faja: nullIfEmptyObject(cleaned.faja),
  componentesEspeciales: Array.isArray(cleaned.componentesEspeciales) ? cleaned.componentesEspeciales : [],
  peticionesEspeciales: Array.isArray(cleaned.peticionesEspeciales) ? cleaned.peticionesEspeciales : [],
  direcciones: Array.isArray(cleaned.direcciones) ? cleaned.direcciones : [],
};

// Asegura que las arrays raíz existen siempre
if (!Array.isArray(out.componentesEspeciales)) out.componentesEspeciales = [];
if (!Array.isArray(out.peticionesEspeciales)) out.peticionesEspeciales = [];
if (!Array.isArray(out.direcciones)) out.direcciones = [];

return [{ json: out }];

function safeItems(name) {
  try {
