// A veces n8n trae el body como string. Lo hacemos robusto:
let resp = $json;
if (typeof resp === "string") resp = JSON.parse(resp);

// Soporta "Full Response" ON/OFF:
const body = resp.body ?? resp;

// En Responses API el texto suele venir en body.output[0..].content[*].text
const text =
  body.output?.flatMap(o => o.content || [])
    ?.find(c => c.type === "output_text")
    ?.text;

if (!text) throw new Error("No encuentro output_text en la respuesta de OpenAI");

let parsed;
try {
  parsed = JSON.parse(text);
