]);

function isEmpty(v) {
  if (v === null || v === undefined) return true;
  if (typeof v === "string" && v.trim() === "") return true;
  if (typeof v === "number" && v === 0) return true;
  if (Array.isArray(v) && v.length === 0) return true;
  if (typeof v === "object" && !Array.isArray(v) && Object.keys(v).length === 0) return true;
  return false;
}

function cleanDeep(value, path) {
  // Arrays: limpiar elementos y eliminar elementos vacíos
  if (Array.isArray(value)) {
    const arr = [];
    for (const el of value) {
      const cleaned = cleanDeep(el, path + "[]");
      // si el elemento queda vacío, lo descartamos
      if (!isEmpty(cleaned)) arr.push(cleaned);
    }
    return arr;
  }

  // Objetos: limpiar claves recursivamente
  if (value && typeof value === "object") {
    const obj = { ...value };
    for (const k of Object.keys(obj)) {
      const childPath = path ? `${path}.${k}` : k;

      obj[k] = cleanDeep(obj[k], childPath);

      // Si está en keepAlwaysPaths, NO lo borramos aunque esté vacío
      if (keepAlwaysPaths.has(childPath)) continue;

      // Si está vacío, borramos
      if (isEmpty(obj[k])) delete obj[k];
    }
    return obj;
  }

  // Primitivos
  return value;
}

// Aplicar limpieza a cada interior
